mapper.sql:
SELECT T1.CONTENT_ID AS contentId, T1.CHANNEL_ID AS channelId, T1.CONTENT_TYPE AS contentType, T1.`STATUS` AS status, T1.SAVE_DATE AS saveTime, T1.SUBMIT_DATE AS submitTime, T1.CHECK_DATE AS checkTime, T4.USER_NAME AS checkUser, T1.VIEW AS view, T1.COMMENT_COUNT AS commentCount, T1.USER_ID AS author, T2.SUMMARY AS summary, T2.TITLE AS title, T3.CHANNEL_NAME AS channelName, T3.CHANNEL_PATH AS channelPath FROM portal_content T1 LEFT JOIN sys_user T4 ON T1.CHECK_USER = T4.USER_ID, portal_content_txt T2, portal_channel T3 WHERE T1.CONTENT_ID = T2.CONTENT_ID AND T1.CHANNEL_ID = T3.CHANNEL_ID AND T1.USER_ID=#{sys_user}
AND T1.CHANNEL_ID = #{channelId}
AND T1.CONTENT_TYPE = #{contentType}
AND T2.TITLE like '%${title}%'
AND T2.summary like '%${summary}%'
AND T1.status=#{status}
ORDER BY CAST(T1.CONTENT_ID AS SIGNED)
