mapper.select:
SELECT su.USER_ID AS userId, su.USER_NAME AS userName, sur.ROLE_ID AS roleId, sur.ADD_USER_ID AS addUserId, sur.ADD_TIME AS addTime, sur.UPDATE_USER_ID AS updateUserId, sur.UPDATE_TIME AS updateTime, sur.VERSION AS version, sr.ROLE_NAME AS roleName, sr.ROLE_DESC AS roleDesc FROM sys_user su, sys_user_role sur, sys_role sr WHERE su.USER_ID = sur.USER_ID AND sur.ROLE_ID = sr.ROLE_ID
AND su.USER_ID = #{userId}
AND sur.ROLE_ID = #{roleId}

SELECT sr.ROLE_ID AS roleId, sr.ROLE_NAME AS roleName, sr.ROLE_DESC AS roleDesc FROM sys_role sr WHERE 1=1
AND sr.ROLE_ID = #{roleId}
AND sr.ROLE_NAME = #{roleName}
