mapper.sql:
SELECT t1.file_md5, t1.file_name, t1.file_loc, t1.file_type, t1.file_size, t1.file_width, t1.file_height, t1.file_time FROM sys_file_base t1 WHERE 1=1
AND t1.file_name like '%${file_name}%'
AND t1.file_type = #{file_type}
AND t1.file_size >= #{param.file_size_start}
AND t1.file_size <= #{param.file_size_end}

update.foreach:
UPDATE sys_file_base

	file_name=${item.file_name}


	file_name=${item.file_loc}


	file_name=${item.file_type}


	file_name=${item.file_size}


	file_name=${item.file_width}


	file_name=${item.file_height}


	file_name=${item.file_time}

WHERE
	file_md5=#{file_md5}
