mapper.sql:
SELECT t1.app_code, t1.file_code, t1.module_code, t1.column_1 t1.column_2, t1.column_3 FROM sys_file_app t1 WHERE 1=1
AND t1.app_code = #{app_code}
AND t1.file_code = #{file_code}
AND t1.module_code = #{module_code}

mapper.delete:
DELETE FROM sys_file_app t1
WHERE 1=1

AND t1.file_code = #{file_code}


AND t1.app_code = #{app_code}


AND t1.module_code = #{module_code}
