mapper.sql:
SELECT t1.app_code, t1.file_code, t1.module_code, t1.file_history, t1.column_1 t1.column_2, t1.column_3 FROM sys_file_app t1 WHERE 1=1
AND t1.app_code = #{app_code}
AND t1.file_code = #{file_code}
AND t1.module_code = #{module_code}

mapper.update:
UPDATE sys_file_app
SET

	file_code = #{file_code},


	app_code = #{app_code},


	module_code = #{module_code},

	file_history = #{file_history},
	column_1 = #{column_1},
	column_2 = #{column_2},
	column_3 = #{column_3}
WHERE 1=1

	AND	file_code = #{file_code}


	AND app_code = #{app_code}


	AND module_code = #{module_code}

mapper.delete:
DELETE FROM sys_file_app t1
WHERE 1=1

AND t1.file_code = #{file_code}


AND t1.app_code = #{app_code}


AND t1.module_code = #{module_code}


AND t1.file_history = #{file_history}
