mapper.sql:
SELECT T1.qxsj_code AS qxsj_code, T1.qxsj_name AS qxsj_name, T1.qxsj_type AS qxsj_type, T1.qxsj_menu AS qxsj_menu, T1.qxsj_sort AS qxsj_sort, T1.qxsj_used AS qxsj_used, T1.qxsj_text AS qxsj_text, T1.add_user_id AS addUserId, T1.upd_user_id AS updUserId, T1.add_time AS addTime, T1.upd_time AS updTime FROM sys_qxsj T1 WHERE 1=1
AND T1.qxsj_code like '%${qxsj_code}%'
AND T1.qxsj_name like '%${qxsj_name}%'
AND T1.qxsj_type = #{qxsj_type}
AND T1.qxsj_used = #{qxsj_used}
ORDER BY T1.qxsj_type,T1.qxsj_sort,T1.qxsj_code
