mapper.sql:
SELECT v.LOG_ID AS loggerId, v.USER_ID AS userId, v.OPERATE_TYPE AS operateType, v.FUN_MODULE AS operateModule, v.OPERATE_REMARK AS operateRemark, v.OPERATE_METHOD AS operateMethod, v.OPETATE_PARAMS AS operateParams, v.OPERATE_TIME AS operateTime, u.USER_NAME AS userName FROM LOG_OPERATE_SERVER v LEFT JOIN SYS_USER u ON u.USER_ID = v.USER_ID WHERE 1 = 1
AND v.FUN_MODULE LIKE CONCAT(CONCAT('%', #{operateModule}), '%')
AND v.USER_ID = #{userId}
AND v.OPERATE_TYPE = #{operateType}
